/**
 * Supervision Validation Demo - Interactive demonstration scenarios
 * Showcases different validation workflows and quality assessment capabilities
 */

export class SupervisionValidationDemo {
  constructor(supervisionService, logger) {
    this.supervisionService = supervisionService;
    this.logger = logger;
    this.demoResults = [];
  }

  /**
   * Demo Scenario 1: Single Image Object Detection Validation
   */
  async demonstrateSingleImageValidation() {
    this.logger.info('üéØ Demo: Single Image Object Detection Validation');
    
    try {
      // Create demo image with known objects
      const demoImage = {
        id: 'demo-single-image-001',
        path: '/demo/images/street_scene.jpg',
        metadata: {
          width: 640,
          height: 480,
          format: 'jpeg',
          source: 'webcam_capture'
        },
        groundTruth: [
          { class: 'person', bbox: [150, 100, 250, 400], confidence: 1.0 },
          { class: 'car', bbox: [300, 200, 500, 350], confidence: 1.0 },
          { class: 'bicycle', bbox: [50, 250, 120, 380], confidence: 1.0 }
        ]
      };

      const modelConfig = {
        type: 'yolov8n',
        weights: '/models/yolov8n.pt',
        confidenceThreshold: 0.5,
        iouThreshold: 0.45
      };

      this.logger.info('üì∏ Processing single image with YOLOv8...');
      const result = await this.supervisionService.validateSingleImage(demoImage, modelConfig);

      this.logger.info('‚úÖ Single Image Validation Results:');
      this.logger.info(`   ‚Ä¢ Image ID: ${result.imageId}`);
      this.logger.info(`   ‚Ä¢ Objects Detected: ${result.detections.count}`);
      this.logger.info(`   ‚Ä¢ Average Confidence: ${result.detections.confidence.toFixed(3)}`);
      this.logger.info(`   ‚Ä¢ Quality Score: ${result.quality.overall_score.toFixed(3)}`);
      this.logger.info(`   ‚Ä¢ Validation: ${result.passed ? 'PASSED' : 'FAILED'}`);

      if (result.detections.objects.length > 0) {
        this.logger.info('   ‚Ä¢ Detected Objects:');
        result.detections.objects.forEach((obj, idx) => {
          this.logger.info(`     ${idx + 1}. ${obj.class} (${obj.confidence.toFixed(3)})`);
        });
      }

      this.demoResults.push({
        scenario: 'single_image_validation',
        result,
        demoNotes: 'Demonstrates basic object detection validation with quality metrics'
      });

      return result;

    } catch (error) {
      this.logger.error('‚ùå Single image validation demo failed:', error.message);
      throw error;
    }
  }

  /**
   * Demo Scenario 2: Batch Processing with Performance Metrics
   */
  async demonstrateBatchProcessing() {
    this.logger.info('üéØ Demo: Batch Processing with Performance Analysis');

    try {
      // Create batch of demo images
      const imageBatch = [
        {
          id: 'demo-batch-001',
          path: '/demo/batch/traffic_001.jpg',
          groundTruth: [
            { class: 'car', bbox: [100, 150, 300, 250], confidence: 1.0 },
            { class: 'person', bbox: [350, 100, 400, 300], confidence: 1.0 }
          ]
        },
        {
          id: 'demo-batch-002',
          path: '/demo/batch/traffic_002.jpg',
          groundTruth: [
            { class: 'car', bbox: [200, 180, 450, 280], confidence: 1.0 },
            { class: 'traffic_light', bbox: [500, 50, 550, 150], confidence: 1.0 }
          ]
        },
        {
          id: 'demo-batch-003',
          path: '/demo/batch/pedestrian_001.jpg',
          groundTruth: [
            { class: 'person', bbox: [150, 100, 250, 400], confidence: 1.0 },
            { class: 'person', bbox: [300, 120, 380, 380], confidence: 1.0 }
          ]
        },
        {
          id: 'demo-batch-004',
          path: '/demo/batch/parking_001.jpg',
          groundTruth: [
            { class: 'car', bbox: [50, 200, 200, 300], confidence: 1.0 },
            { class: 'car', bbox: [250, 180, 400, 280], confidence: 1.0 },
            { class: 'car', bbox: [450, 190, 600, 290], confidence: 1.0 }
          ]
        }
      ];

      const modelConfig = {
        type: 'yolov8s',
        weights: '/models/yolov8s.pt',
        batchSize: 2,
        confidenceThreshold: 0.6
      };

      this.logger.info(`üì¶ Processing batch of ${imageBatch.length} images...`);
      const startTime = Date.now();
      
      const result = await this.supervisionService.validateBatch(imageBatch, modelConfig);
      
      const totalTime = Date.now() - startTime;

      this.logger.info('‚úÖ Batch Processing Results:');
      this.logger.info(`   ‚Ä¢ Total Images: ${result.summary.totalImages}`);
      this.logger.info(`   ‚Ä¢ Successfully Processed: ${result.summary.processedSuccessfully}`);
      this.logger.info(`   ‚Ä¢ Failed: ${result.summary.failed}`);
      this.logger.info(`   ‚Ä¢ Success Rate: ${(result.summary.successRate * 100).toFixed(1)}%`);
      this.logger.info(`   ‚Ä¢ Total Processing Time: ${totalTime}ms`);
      this.logger.info(`   ‚Ä¢ Average per Image: ${result.performance.averageProcessingTime.toFixed(0)}ms`);
      this.logger.info(`   ‚Ä¢ Overall Quality Score: ${result.aggregatedQuality.overall_score.toFixed(3)}`);

      // Show per-image results
      this.logger.info('   ‚Ä¢ Per-Image Results:');
      result.results.forEach((imgResult, idx) => {
        if (imgResult.passed) {
          this.logger.info(`     ${idx + 1}. ${imgResult.imageId}: ‚úÖ ${imgResult.detections.count} objects (${imgResult.quality.overall_score.toFixed(3)})`);
        } else {
          this.logger.info(`     ${idx + 1}. ${imgResult.imageId}: ‚ùå ${imgResult.error || 'Validation failed'}`);
        }
      });

      this.demoResults.push({
        scenario: 'batch_processing',
        result,
        demoNotes: 'Demonstrates batch processing efficiency and aggregated quality metrics'
      });

      return result;

    } catch (error) {
      this.logger.error('‚ùå Batch processing demo failed:', error.message);
      throw error;
    }
  }

  /**
   * Demo Scenario 3: Real-Time Stream Validation
   */
  async demonstrateRealTimeValidation() {
    this.logger.info('üéØ Demo: Real-Time Stream Validation');

    try {
      const streamConfig = {
        source: 'demo_webcam',
        targetFps: 15,
        resolution: { width: 640, height: 480 },
        bufferSize: 30
      };

      const modelConfig = {
        type: 'yolov8n',
        weights: '/models/yolov8n.pt',
        realTime: true,
        confidenceThreshold: 0.4
      };

      this.logger.info('üìπ Starting real-time stream validation...');
      this.logger.info(`   ‚Ä¢ Target FPS: ${streamConfig.targetFps}`);
      this.logger.info(`   ‚Ä¢ Resolution: ${streamConfig.resolution.width}x${streamConfig.resolution.height}`);

      const streamValidator = await this.supervisionService.validateRealTimeStream(
        streamConfig,
        modelConfig
      );

      // Let it run for demo period
      this.logger.info('‚è±Ô∏è  Running stream for 10 seconds...');
      await new Promise(resolve => setTimeout(resolve, 10000));

      // Stop and get final metrics
      await streamValidator.stop();

      this.logger.info('‚úÖ Real-Time Stream Results:');
      this.logger.info(`   ‚Ä¢ Stream ID: ${streamValidator.streamId}`);
      this.logger.info(`   ‚Ä¢ Frames Processed: ${streamValidator.metrics.framesProcessed}`);
      this.logger.info(`   ‚Ä¢ Average FPS: ${streamValidator.metrics.averageFps.toFixed(1)}`);
      this.logger.info(`   ‚Ä¢ Average Latency: ${streamValidator.metrics.latencyMs.toFixed(1)}ms`);
      this.logger.info(`   ‚Ä¢ Detection Accuracy: ${(streamValidator.metrics.detectionAccuracy * 100).toFixed(1)}%`);

      this.demoResults.push({
        scenario: 'real_time_stream',
        result: {
          streamId: streamValidator.streamId,
          finalMetrics: streamValidator.metrics
        },
        demoNotes: 'Demonstrates real-time processing capabilities with FPS and latency metrics'
      });

      return streamValidator;

    } catch (error) {
      this.logger.error('‚ùå Real-time stream demo failed:', error.message);
      throw error;
    }
  }

  /**
   * Demo Scenario 4: Annotation Quality Assessment
   */
  async demonstrateQualityAssessment() {
    this.logger.info('üéØ Demo: Annotation Quality Assessment');

    try {
      // Demo annotations with deliberate quality variations
      const annotations = {
        info: {
          description: 'Demo annotation quality assessment',
          version: '1.0',
          year: 2024
        },
        images: [
          {
            id: 'quality-demo-001',
            file_name: 'quality_test_001.jpg',
            width: 640,
            height: 480
          }
        ],
        annotations: [
          // High quality annotation
          {
            id: 1,
            image_id: 'quality-demo-001',
            category_id: 1,
            bbox: [100, 100, 150, 200],
            area: 30000,
            segmentation: [],
            iscrowd: 0
          },
          // Lower quality annotation (slightly off)
          {
            id: 2,
            image_id: 'quality-demo-001',
            category_id: 2,
            bbox: [300, 150, 120, 180],
            area: 21600,
            segmentation: [],
            iscrowd: 0
          }
        ],
        categories: [
          { id: 1, name: 'person', supercategory: 'person' },
          { id: 2, name: 'car', supercategory: 'vehicle' }
        ]
      };

      const groundTruth = {
        info: {
          description: 'Ground truth for quality assessment',
          version: '1.0'
        },
        images: [
          {
            id: 'quality-demo-001',
            file_name: 'quality_test_001.jpg',
            width: 640,
            height: 480
          }
        ],
        annotations: [
          // Perfect ground truth
          {
            id: 1,
            image_id: 'quality-demo-001',
            category_id: 1,
            bbox: [98, 102, 152, 198],
            area: 30096,
            segmentation: [],
            iscrowd: 0
          },
          {
            id: 2,
            image_id: 'quality-demo-001',
            category_id: 2,
            bbox: [305, 145, 125, 185],
            area: 23125,
            segmentation: [],
            iscrowd: 0
          }
        ],
        categories: [
          { id: 1, name: 'person', supercategory: 'person' },
          { id: 2, name: 'car', supercategory: 'vehicle' }
        ]
      };

      this.logger.info('üîç Assessing annotation quality...');
      const qualityResult = await this.supervisionService.assessAnnotationQuality(
        annotations,
        groundTruth
      );

      this.logger.info('‚úÖ Annotation Quality Assessment Results:');
      this.logger.info(`   ‚Ä¢ Overall Score: ${qualityResult.overall_score.toFixed(3)}`);
      this.logger.info(`   ‚Ä¢ Precision: ${qualityResult.metrics.precision.toFixed(3)}`);
      this.logger.info(`   ‚Ä¢ Recall: ${qualityResult.metrics.recall.toFixed(3)}`);
      this.logger.info(`   ‚Ä¢ F1 Score: ${qualityResult.metrics.f1_score.toFixed(3)}`);
      this.logger.info(`   ‚Ä¢ Consistency Score: ${qualityResult.consistency.score.toFixed(3)}`);
      this.logger.info(`   ‚Ä¢ Completeness Score: ${qualityResult.completeness.score.toFixed(3)}`);
      this.logger.info(`   ‚Ä¢ Assessment: ${qualityResult.passed ? 'PASSED' : 'NEEDS IMPROVEMENT'}`);

      if (qualityResult.recommendations.length > 0) {
        this.logger.info('   ‚Ä¢ Recommendations:');
        qualityResult.recommendations.forEach((rec, idx) => {
          this.logger.info(`     ${idx + 1}. ${rec}`);
        });
      }

      this.demoResults.push({
        scenario: 'quality_assessment',
        result: qualityResult,
        demoNotes: 'Demonstrates annotation quality evaluation with precision/recall metrics'
      });

      return qualityResult;

    } catch (error) {
      this.logger.error('‚ùå Quality assessment demo failed:', error.message);
      throw error;
    }
  }

  /**
   * Demo Scenario 5: Performance Benchmarking
   */
  async demonstratePerformanceBenchmark() {
    this.logger.info('üéØ Demo: Performance Benchmarking');

    try {
      const benchmarkConfig = {
        modelType: 'yolov8s',
        testDataset: '/demo/benchmark_dataset',
        metrics: ['throughput', 'latency', 'accuracy', 'memory_usage'],
        iterations: 25,
        batchSizes: [1, 2, 4],
        imageResolutions: [
          { width: 416, height: 416 },
          { width: 640, height: 640 }
        ],
        deviceTypes: ['cpu'],
        warmupRuns: 5
      };

      this.logger.info('üèÉ Running performance benchmark...');
      this.logger.info(`   ‚Ä¢ Model: ${benchmarkConfig.modelType}`);
      this.logger.info(`   ‚Ä¢ Iterations: ${benchmarkConfig.iterations}`);
      this.logger.info(`   ‚Ä¢ Batch Sizes: [${benchmarkConfig.batchSizes.join(', ')}]`);

      const benchmarkResult = await this.supervisionService.runPerformanceBenchmark(
        benchmarkConfig
      );

      this.logger.info('‚úÖ Performance Benchmark Results:');
      this.logger.info(`   ‚Ä¢ Benchmark ID: ${benchmarkResult.benchmarkId}`);
      this.logger.info(`   ‚Ä¢ Throughput: ${benchmarkResult.results.throughput.toFixed(1)} FPS`);
      this.logger.info(`   ‚Ä¢ Average Latency: ${benchmarkResult.results.latency.toFixed(1)}ms`);
      this.logger.info(`   ‚Ä¢ Accuracy: ${(benchmarkResult.results.accuracy * 100).toFixed(1)}%`);
      this.logger.info(`   ‚Ä¢ CPU Usage: ${benchmarkResult.results.resourceUsage.cpu.toFixed(1)}%`);
      this.logger.info(`   ‚Ä¢ Memory Usage: ${benchmarkResult.results.resourceUsage.memory}MB`);
      this.logger.info(`   ‚Ä¢ Benchmark: ${benchmarkResult.passed ? 'PASSED' : 'NEEDS OPTIMIZATION'}`);

      if (benchmarkResult.recommendations.length > 0) {
        this.logger.info('   ‚Ä¢ Performance Recommendations:');
        benchmarkResult.recommendations.forEach((rec, idx) => {
          this.logger.info(`     ${idx + 1}. ${rec}`);
        });
      }

      this.demoResults.push({
        scenario: 'performance_benchmark',
        result: benchmarkResult,
        demoNotes: 'Demonstrates performance profiling with throughput and resource usage metrics'
      });

      return benchmarkResult;

    } catch (error) {
      this.logger.error('‚ùå Performance benchmark demo failed:', error.message);
      throw error;
    }
  }

  /**
   * Generate comprehensive demo report
   */
  async generateDemoReport() {
    this.logger.info('üìä Generating comprehensive demo report...');

    try {
      const report = await this.supervisionService.generateValidationReport(
        this.demoResults.map(demo => demo.result)
      );

      // Enhance report with demo-specific information
      const enhancedReport = {
        ...report,
        demoInfo: {
          title: 'Supervision Validation Service Demo Report',
          timestamp: new Date().toISOString(),
          scenarios: this.demoResults.map(demo => ({
            scenario: demo.scenario,
            description: demo.demoNotes,
            status: demo.result.passed !== false ? 'SUCCESS' : 'NEEDS_ATTENTION'
          })),
          summary: {
            totalScenarios: this.demoResults.length,
            successfulScenarios: this.demoResults.filter(demo => 
              demo.result.passed !== false
            ).length,
            demonstratedCapabilities: [
              'Single image object detection',
              'Batch processing with performance metrics',
              'Real-time stream validation',
              'Annotation quality assessment',
              'Performance benchmarking'
            ]
          }
        }
      };

      this.logger.info('‚úÖ Demo Report Generated:');
      this.logger.info(`   ‚Ä¢ Report ID: ${enhancedReport.reportId}`);
      this.logger.info(`   ‚Ä¢ Total Scenarios: ${enhancedReport.demoInfo.summary.totalScenarios}`);
      this.logger.info(`   ‚Ä¢ Successful: ${enhancedReport.demoInfo.summary.successfulScenarios}`);
      this.logger.info(`   ‚Ä¢ Export Formats: JSON, HTML, PDF`);

      if (enhancedReport.exportPaths) {
        this.logger.info('   ‚Ä¢ Export Paths:');
        Object.entries(enhancedReport.exportPaths).forEach(([format, path]) => {
          this.logger.info(`     ‚Ä¢ ${format.toUpperCase()}: ${path}`);
        });
      }

      return enhancedReport;

    } catch (error) {
      this.logger.error('‚ùå Demo report generation failed:', error.message);
      throw error;
    }
  }

  /**
   * Run complete demo workflow
   */
  async runCompleteDemo() {
    this.logger.info('üöÄ Starting Complete Supervision Validation Demo');
    this.logger.info('=' * 60);

    try {
      // Clear previous results
      this.demoResults = [];

      // Run all demo scenarios
      await this.demonstrateSingleImageValidation();
      this.logger.info('');

      await this.demonstrateBatchProcessing();
      this.logger.info('');

      await this.demonstrateRealTimeValidation();
      this.logger.info('');

      await this.demonstrateQualityAssessment();
      this.logger.info('');

      await this.demonstratePerformanceBenchmark();
      this.logger.info('');

      // Generate final report
      const finalReport = await this.generateDemoReport();

      this.logger.info('');
      this.logger.info('üéâ Complete Demo Finished Successfully!');
      this.logger.info('=' * 60);
      this.logger.info('All Supervision validation capabilities demonstrated.');
      this.logger.info(`Check ${finalReport.exportPaths?.html || 'the generated report'} for detailed results.`);

      return {
        success: true,
        demoResults: this.demoResults,
        finalReport
      };

    } catch (error) {
      this.logger.error('‚ùå Complete demo failed:', error.message);
      return {
        success: false,
        error: error.message,
        partialResults: this.demoResults
      };
    }
  }

  /**
   * Get demo results summary
   */
  getDemoSummary() {
    return {
      totalScenarios: this.demoResults.length,
      scenarios: this.demoResults.map(demo => ({
        name: demo.scenario,
        status: demo.result.passed !== false ? 'SUCCESS' : 'FAILED',
        notes: demo.demoNotes
      })),
      capabilities: [
        'Object detection validation',
        'Batch processing with metrics',
        'Real-time stream analysis',
        'Annotation quality scoring',
        'Performance benchmarking',
        'Comprehensive reporting'
      ]
    };
  }
}